import './App.css'
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

// Connect to the Socket.IO server at localhost:5000
const socket = io('http://localhost:5000');

function App() {
  // State to store user's name
  const [name, setName] = useState('');
  // State to store the current message typed by the user
  const [message, setMessage] = useState('');
  // State to store the list of all messages exchanged in the chat
  const [messages, setMessages] = useState([]); 
  // State to check if the user has entered their name
  const [isNameSet, setIsNameSet] = useState(false);

  // useEffect will run once when the component is first rendered
  useEffect(() => {
    // When a new chat message is received from the server, add it to the messages array
    socket.on('chatMessage', (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    // Clean up the socket listener when the component is unmounted (e.g. when the user leaves the page)
    return () => {
      socket.off('chatMessage');
    };
  }, []); // Empty array ensures this effect runs only once

  // Function to handle form submission when user enters their name
  const handleNameSubmit = (e) => {
    e.preventDefault(); // Prevents page from reloading
    if (name) {
      setIsNameSet(true); // After entering the name, show the chat window
    }
  };

  // Function to send a message when the user submits the message form
  const sendMessage = (e) => {
    e.preventDefault(); // Prevents page from reloading
    if (message) {
      // Send the name and message to the server
      socket.emit('chatMessage', { name, message });
      setMessage(''); // Clear the input field after sending the message
    }
  };

  return (
    <div className="App">
      {/* If the user hasn't entered a name yet, show the name input form */}
      {!isNameSet ? (
        <form onSubmit={handleNameSubmit}>
          <h2>Enter your name to join the chat</h2>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter your name"
            required
          />
          <button type="submit">Join Chat</button>
        </form>
      ) : (
        // If the name is set, show the chat window
        <div className="chat-window">
          <h1>Welcome, {name}!</h1>
          <div className="messages">
            {/* Display each message in the chat */}
            {messages.map((msg, index) => (
              <div key={index}>
                <strong>{msg.name}: </strong>{msg.message}
              </div>
            ))}
          </div>
          <form onSubmit={sendMessage}>
            <input
              type="text"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Enter your message"
              required
            />
            <button type="submit">Send</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
